import express from 'express'
import userRoutes from '../routes/user.routes';
import morgan from 'morgan'


const app = express();

// settings
app.set('port', process.env.PORT || 7000);

//middlewares
app.use(morgan('dev'));
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// routes
app.use(userRoutes);

export default app;

